module.exports = function csvCrank(srcArr, rerip) {
    let skuArr = []
    const fs = require('fs')
    for(var i = 0; i < srcArr.length; i++){
    let errorSKU = false
    for(var k = 0; k < rerip.length; k++){
        if(srcArr[i].sku == rerip[k]){
            errorSKU = true
            break
        }
    }
    let x = srcArr[i].chunk.join('\n')
    if(errorSKU == true){
        fs.appendFileSync('./rerip.csv', x+'\n')
        skuArr.push(srcArr[i].sku)
    }else{
        fs.appendFileSync('./submit.csv', x+'\n')
    }
    if(i == srcArr.length/4){console.log('25% complete\n')}
    if(i == srcArr.length/2){console.log('50% complete\n')}
    if(i == srcArr.length/4+srcArr.length/2){console.log('75% complete\n')}
    if(i == srcArr.length-1){{console.log('100% complete\n')}}
}
return skuArr
}