"use strict";
import getPermutations from 'get-permutations';

function uniq(arr) {
    const sorted = arr.sort();

    let result = [arr[0]];
    for (var i = 1; i < sorted.length; i++) {
        if (sorted[i - 1].join('') !== sorted[i].join('')) {
            result.push(sorted[i]);
        }
    }
    return result;
}

const getUniquePermutations = (arr) => {
    return uniq(getPermutations(arr));
};

export default getUniquePermutations;