/*global describe it*/
import assert from 'assert';
import getUniquePermutations from '../src/getUniquePermutations.js';

describe('getUniquePermutations() should...', function () {
    it(`perumtate array with chars`, function () {
        const permutations = getUniquePermutations(['a','b']);
        assert.deepEqual(permutations, [['a','b'],['b','a']]);
    });

    it(`perumtate array with numbers`, function () {
        const permutations = getUniquePermutations([1,0,1]);
        assert.deepEqual(permutations, [[0,1,1],[1,0,1],[1,1,0]]);
    });
});