module.exports = function reripArrFunc(err, srcArr) {
    var rerip = []
for(var i = 0; i < err.length; i++){
    for(var k = 0; k < srcArr.length; k++){
        for(var l = 0; l < srcArr[k].chunk.length; l++){
            let x = srcArr[k].chunk[l].split(',')
            let y = 0
            if(x[x.length-2].length < 1){
                 y = x[x.length-1]
            }else{ y = x[x.length-2]}
            if(err[i] == y){
                rerip.push(srcArr[k].sku)
            }
        }
    }
}
return rerip
}