module.exports = function srcArr(src, linesPer) {
    var srcArr = []
    var arr = []
    for(var i = 0; i < src.length; i = i + linesPer){
    if(src[i].length > 1){
        let x = src[i].split(',')
        let y = x[x.length-12].split('-')
        var arr = []
        for(var k = 0; k < linesPer; k++){
            arr.push(src[i+k])
        }
        srcArr.push({sku: y[0], chunk: arr})
    }
}
return srcArr
}