module.exports = function csvCombination(csvArr) {
    const fs = require('fs')
    var src = []
    for(var i = 0; i < csvArr.length; i++){
        let data = fs.readFileSync(`C:/Users/heliumface770/Desktop/csv-cleanup/csvs/${csvArr[i]}`, 'utf-8').split('\r\n')
        if(data.length == 1){
            data = fs.readFileSync(`C:/Users/heliumface770/Desktop/csv-cleanup/csvs/${csvArr[i]}`, 'utf-8').split('\n')
        }
        for(var k = 0; k < data.length; k++){
            if(data.length-1 !== k && data[k] !== ''){
                src.push(data[k])
            }
        }
    }
    return src
}